mapper.select:
select o.*, wui.nick_name
from t_order o
    left join t_wx_user_info wui on o.user_id = wui.open_id

    o.deleted = 0

        and o.order_no like concat('%', #{orderNo}, '%')


        and o.status = #{status}


        and o.delivery_date = #{dateValue}


order by o.create_time desc

    limit #{start}, #{pageSize}

select count(*)
from t_order o

    o.deleted = 0

        and o.order_no like concat('%', #{orderNo}, '%')


        and o.status = #{status}


        and o.delivery_date = #{dateValue}

mapper.update:
update t_order o
set o.status = '7'
where o.status = '2' and delivery_date = #{deliveryDate}
